CREATE TABLE lista_pau_brasil (
    id INT AUTO_INCREMENT PRIMARY KEY,
    item VARCHAR(10) NOT NULL,
    desc_item VARCHAR(255) NOT NULL,
    descricao TEXT NULL
);


